#Now add in lines of code to calculate GC content of the above DNA string. Run with test data first!!"""
toyseq = "AATTGGCCA"
def AT_content_calculator(seq):
	SEQ = seq.upper()
	SEQ_length = len(SEQ)
	SEQA = SEQ.count('A')
	SEQT = SEQ.count('T')
	proportionATinSEQ = (SEQA + SEQT)/SEQ_length
	return proportionATinSEQ

print("Toy AT content",AT_content_calculator(toyseq))
real_seq = ""
print("real sequence AT content", AT_content_calculator(real_seq))

def GC_content_calculator(seq):
	SEQ = seq.upper()
	SEQ_length = len(SEQ)
	SEQG = SEQ.count('G')
	SEQC = SEQ.count('C')
	proportionGCinSEQ = (SEQG + SEQC)/SEQ_length
	return proportionGCinSEQ

print("real seq GC content", GC_content_calculator(real_seq))

