# 13. Write a new script that prints out the reverse complement of the above DNA string. Hint for reverse. Use string formating for printing.
above_DNA_string = seq_to_cut[101:201].upper()
complement = ""
def get_complement_seq(seq):
	global complement
	for nt in seq:
		if nt == 'A':
			complement = complement + 'T'
		if nt == 'T':
			complement = complement + 'A'
		if nt == 'G':
			complement = complement + 'C'
		if nt == 'C':
			complement = complement + 'G'
	return complement	
#print(get_complement_seq(above_DNA_string))
complement_above_string = get_complement_seq(above_DNA_string)
reverse_complement_above_DNA_sting = complement_above_string[::-1]
nl = "\n"
print(f"Original sequence: 5' {above_DNA_string} 3'{nl}Complement: 5' {complement_above_string} 3'{nl}Reverse complement: 5' {reverse_complement_above_DNA_sting}  3'")

# 14 Write a script to find the starting nucleotide position of an EcoRI site in the below DNA sequence. Remember DNA sequences start with a 1 and a python string starts with a 0. Run with test data first.
eco_dna = ""
ecori = "GAATTC"
ecorirv = "CTTAAG"
count = 0
yay = 0

def restriction_find(seq):
	global count
	SEQ = seq.upper()
	while count < len(seq)-5:
		#print(count)
		subset = seq[count: count+6]
		#print(subset)
		if subset == ecori or subset == ecorirv:
			yay = 1
			print ("Yay motif was found at starting position:", count, "with subset being:", subset, "and eori:", ecori, ecorirv)
			print(f"EcoRI startPos:{count} endPos:{count+6}")
		count = count + 1
	if yay == 0:
		print("no EcoRI sites were found")
	return

restriction_find(eco_dna)	

